CREATE OR REPLACE PROCEDURE pls_desvincular_agravo_seg ( nr_seq_segurado_agravo_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_mens_gerada_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_mens_gerada_w
from	pls_segurado_agravo_parc
where	nr_seq_segurado_agravo	= nr_seq_segurado_agravo_p
and	(nr_seq_mensalidade_item IS NOT NULL AND nr_seq_mensalidade_item::text <> '');

if (qt_mens_gerada_w = 0) then
	delete	from	pls_segurado_agravo_parc
	where	nr_seq_segurado_agravo	= nr_seq_segurado_agravo_p;

	delete	from	pls_segurado_agravo
	where	nr_sequencia	= nr_seq_segurado_agravo_p;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265927,'');
	--Não é possível desvincular o agravo, pois já foi gerado mensalidade para o mesmo. Favor verifique!
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
