CREATE OR REPLACE PROCEDURE pls_desvincular_cred_nao_iden (nr_seq_cred_nao_ident_p bigint, nr_seq_lote_deposito_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_titulo_w		bigint;

C01 CURSOR FOR
	SELECT	nr_titulo
	from	deposito_ident_titulo
	where	nr_seq_deposito = nr_seq_lote_deposito_p;


BEGIN
if (nr_seq_cred_nao_ident_p IS NOT NULL AND nr_seq_cred_nao_ident_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		delete	from movto_banco_pend_baixa
		where	nr_titulo	      = nr_titulo_w
		and	nr_seq_movto_pend     = nr_seq_cred_nao_ident_p
		and	nr_seq_deposito_ident = nr_seq_lote_deposito_p;

		end;
	end loop;
	close C01;

	CALL atualizar_saldo_movto_bco_pend(nr_seq_cred_nao_ident_p,nm_usuario_p,'N');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
