SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desvincular_pf_inclusao ( nr_seq_solicitacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w		bigint;
ie_etapa_solicitacao_w	varchar(10);
nr_seq_intercambio_w		bigint;


BEGIN

begin
select	max(nr_seq_intercambio)
into STRICT	nr_seq_intercambio_w
from	pls_inclusao_beneficiario
where	nr_sequencia		= nr_seq_solicitacao_p;
exception
when others then
	nr_seq_intercambio_w	:= null;
end;

/*aaschlote 15/05/2012 - 445294 - Quando for intercâmbio alterar a etapa de solicitação de intercâmbio*/

if (coalesce(nr_seq_intercambio_w::text, '') = '') then
	select	count(*)
	into STRICT	qt_registros_w
	from	pls_homonimo_pessoa_fisica
	where	nr_seq_inclusao_benef	= nr_seq_solicitacao_p;

	if (qt_registros_w	= 0) then
		ie_etapa_solicitacao_w	:= 'AG';
	else
		ie_etapa_solicitacao_w	:= 'CH';
	end if;

	update	pls_inclusao_beneficiario
	set	cd_pessoa_fisica	= '',
		nm_usuario		= nm_usuario_p,
		ie_etapa_solicitacao	= ie_etapa_solicitacao_w,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_solicitacao_p;
else
	update	pls_inclusao_beneficiario
	set	cd_pessoa_fisica		= '',
		nm_usuario			= nm_usuario_p,
		IE_ETAPA_SOLIC_INTERCAMBIO	= 'AG',
		dt_atualizacao			= clock_timestamp()
	where	nr_sequencia			= nr_seq_solicitacao_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
