CREATE OR REPLACE PROCEDURE pls_desvincular_proc_conta_gru ( nr_seq_proc_conta_p pls_processo_conta.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


cont_w			bigint;
nr_seq_proc_gru_w	bigint;


BEGIN

select	nr_seq_proc_gru
into STRICT	nr_seq_proc_gru_w
from	pls_processo_conta
where	nr_sequencia	= nr_seq_proc_conta_p;

select	count(*)
into STRICT	cont_w
from	titulo_pagar
where	nr_seq_proc_gru	= nr_seq_proc_gru_w
and	ie_situacao	<> 'C';

if (cont_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(309264); -- Esta GRU já possui título a pagar gerado! Não é possível desvincular esta conta desta GRU.
end if;

update	pls_processo_conta
set	nr_seq_proc_gru	 = NULL
where	nr_sequencia	= nr_seq_proc_conta_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
