CREATE OR REPLACE PROCEDURE pls_desvincular_protocolo_lote ( nr_seq_protocolo_p bigint, nr_seq_lote_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_protocolos_lote_w		bigint;
nr_seq_conta_w			bigint;
nr_seq_lote_conta_w			pls_protocolo_conta.nr_seq_lote_conta%type;
ds_historico_w			pls_prot_conta_hist.ds_historico%type;

C01 CURSOR FOR
	SELECT	b.nr_seq_lote_conta
	from	pls_conta		a,
		pls_protocolo_conta	b
	where	a.nr_seq_protocolo 	= b.nr_sequencia
	and	a.nr_seq_protocolo	= nr_seq_protocolo_p;

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Desvincular o protocolo de um lote de protocolos e desfazer os vínculos com a análise
--------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X ] Tasy (Delphi/Java) [  ] Portal [  ] Relatórios [ ] Outros:
 -------------------------------------------------------------------------------------
Pontos de atenção:
 ++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
BEGIN

for r_C01_w in C01 loop
	nr_seq_lote_conta_w 	:= r_C01_w.nr_seq_lote_conta;
end loop;

update	pls_protocolo_conta
set  	nr_seq_lote_conta	 = NULL
where   nr_sequencia		= nr_seq_protocolo_p;

commit;

ds_historico_w	:= 'Desvinculado do lote '|| nr_seq_lote_conta_w;

insert into pls_prot_conta_hist(nr_sequencia,
	ds_historico,
	dt_atualizacao,
	dt_atualizacao_nrec,
	dt_historico,
	ie_tipo_historico,
	nm_usuario,
	nm_usuario_historico,
	nm_usuario_nrec,
	nr_seq_protocolo)
values (nextval('pls_prot_conta_hist_seq'),
	ds_historico_w,
	clock_timestamp(),
	clock_timestamp(),
	clock_timestamp(),
	'18',
	nm_usuario_p,
	nm_usuario_p,
	nm_usuario_p,
	nr_seq_protocolo_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
