CREATE OR REPLACE PROCEDURE pls_desvincular_regra_reajuste ( nr_seq_reajuste_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w		integer;


BEGIN

select	count(1)
into STRICT	qt_registro_w
from	pls_reajuste_tabela
where	nr_seq_reajuste	= nr_seq_reajuste_p;

if (qt_registro_w	> 0) then
	delete	from pls_reajuste_tabela
	where 	nr_seq_reajuste	= nr_seq_reajuste_p;
end if;

select	count(1)
into STRICT	qt_registro_w
from	pls_reajuste_cobr_retro
where	nr_seq_reajuste	= nr_seq_reajuste_p;

if (qt_registro_w	> 0) then
	delete	from pls_reajuste_cobr_retro
	where 	nr_seq_reajuste	= nr_seq_reajuste_p;
end if;

update	pls_reajuste
set	dt_periodo_inicial	 = NULL,
	dt_periodo_final	 = NULL,
	ds_oficio_ans		 = NULL,
	nr_protocolo_ans	 = NULL,
	dt_autorizacao_ans	 = NULL,
	ds_observacao		 = NULL,
	nr_seq_regra		 = NULL,
	tx_reajuste		 = NULL,
	ie_indice_reajuste_contrato  = NULL
where	nr_sequencia	= nr_seq_reajuste_p;

CALL pls_excluir_reaj_copartic(nr_seq_reajuste_p);
CALL pls_excluir_reaj_inscricao(nr_seq_reajuste_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
