SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desvincular_titulo_aberto ( nr_seq_lote_p bigint, nr_seq_titulo_aberto_p bigint) AS $body$
DECLARE


vl_titulo_w	double precision;
vl_lote_w	double precision;
vl_lote_novo_w	double precision;
nr_titulo_w	bigint;


BEGIN

select	max(nr_titulo)
into STRICT	nr_titulo_w
from	pls_desc_reenvio
where	nr_sequencia	= nr_seq_titulo_aberto_p;

delete	from pls_desc_reenvio_hist
where	nr_seq_tit_reenvio	= nr_seq_titulo_aberto_p;

delete	from pls_desc_reenvio
where	nr_seq_lote	= nr_seq_lote_p
and	nr_titulo	= nr_titulo_w;

select	max(vl_titulo)
into STRICT	vl_titulo_w
from	titulo_receber
where	nr_titulo	= nr_titulo_w;

select	max(vl_lote)
into STRICT	vl_lote_w
from	pls_desc_lote_reenvio
where	nr_sequencia	= nr_seq_lote_p;

vl_lote_novo_w	:= coalesce(vl_lote_w,0) - coalesce(vl_titulo_w,0);

update	pls_desc_lote_reenvio
set	vl_lote		= vl_lote_novo_w
where	nr_sequencia	= nr_seq_lote_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
