SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_desvincular_titulo_lote ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE

 
nr_titulo_w			bigint;
nr_seq_nota_w			bigint;
ie_mes_fechado_w		varchar(5);
nr_titulo_vinc_w		bigint;

C00 CURSOR FOR 
	SELECT	nr_sequencia 
	from	nota_fiscal 
	where	nr_seq_lote_res_pls	= nr_seq_lote_p 
	and	ie_situacao	= '1';

C01 CURSOR FOR 
	SELECT	a.nr_titulo 
	FROM titulo_pagar a
LEFT OUTER JOIN nota_fiscal b ON (a.nr_seq_nota_fiscal = b.nr_sequencia)
WHERE b.nr_sequencia	= nr_seq_nota_w and a.ie_situacao	= 'A';

C02 CURSOR FOR 
	SELECT	a.nr_titulo 
	from	titulo_pagar a 
	where	a.ie_situacao = 'A' 
	and	a.nr_seq_lote_res_pls = nr_seq_lote_p;
	

BEGIN 
 
select	pls_obter_se_mes_fechado(dt_mes_competencia,'T',cd_estabelecimento_p) 
into STRICT	ie_mes_fechado_w 
from	pls_lote_protocolo 
where	nr_sequencia	= nr_seq_lote_p;
 
if (ie_mes_fechado_w	= 'S') then 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(244224);
end if;
 
open C00;
loop 
fetch C00 into	 
	nr_seq_nota_w;
EXIT WHEN NOT FOUND; /* apply on C00 */
	begin 
	if (coalesce(nr_seq_nota_w,0) <> 0) then 
		CALL estornar_nota_fiscal(nr_seq_nota_w, nm_usuario_p);
		 
		/*Alterado para limpar o numero da NF no lote poís aquela nota não poderá mais ser utilizada - drquadros 604561*/
 
		begin 
			update	pls_lote_protocolo 
			set	nr_nota_fiscal  = NULL 
			where	nr_sequencia  = nr_seq_lote_p;
		exception 
		when others then 
			CALL wheb_mensagem_pck.exibir_mensagem_abort(244223);
		end;
	end if;
	 
	open C01;
	loop 
		fetch C01 into	 
		nr_titulo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin 
		CALL cancelar_titulo_pagar(nr_titulo_w, nm_usuario_p, clock_timestamp());
		end;
	end loop;
	close C01;
	 
	end;
end loop;
close C00;
 
open C02;
loop 
fetch C02 into	 
	nr_titulo_vinc_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin 
	CALL cancelar_titulo_pagar(nr_titulo_vinc_w, nm_usuario_p, clock_timestamp());
	end;
end loop;
close C02;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_desvincular_titulo_lote ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) FROM PUBLIC;

