CREATE OR REPLACE PROCEDURE pls_desvincular_tit_enc_contas ( nr_seq_titulo_pessoa_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_titulo_receber_w	bigint;
nr_titulo_pagar_w	bigint;
nr_seq_pessoa_w		bigint;
nr_seq_lote_w		bigint;


BEGIN

if (nr_seq_titulo_pessoa_p IS NOT NULL AND nr_seq_titulo_pessoa_p::text <> '') then
	select	nr_titulo_receber,
		nr_titulo_pagar,
		nr_seq_pessoa
	into STRICT	nr_titulo_receber_w,
		nr_titulo_pagar_w,
		nr_seq_pessoa_w
	from	encontro_contas_item a
	where	a.nr_sequencia	= nr_seq_titulo_pessoa_p;

	delete from encontro_contas_item
	where	nr_sequencia = nr_seq_titulo_pessoa_p;

	select	max(a.nr_sequencia)
	into STRICT	nr_seq_lote_w
	from	lote_encontro_contas a,
		pessoa_encontro_contas b
	where	a.nr_sequencia	= b.nr_seq_lote
	and	b.nr_sequencia	= nr_seq_pessoa_w;

	insert into lote_encontro_contas_hist(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_lote,
		nr_titulo_receber,
		nr_titulo_pagar,
		ie_tipo_alteracao)
	values (nextval('pls_lote_camara_comp_hist_seq'),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_lote_w,
		nr_titulo_receber_w,
		nr_titulo_pagar_w,
		'E');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
