CREATE OR REPLACE PROCEDURE pls_desvinc_protocolo_lote ( nr_seq_protocolo_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint ) AS $body$
DECLARE


nr_seq_lote_conta_w	bigint;
dt_geracao_analise_w	timestamp;
ds_historico_w		pls_prot_conta_hist.ds_historico%type;


BEGIN

select	max(nr_seq_lote_conta)
into STRICT	nr_seq_lote_conta_w
from	pls_protocolo_conta
where	nr_sequencia	= nr_seq_protocolo_p;

select	max(dt_geracao_analise)
into STRICT	dt_geracao_analise_w
from	pls_lote_protocolo_conta
where	nr_sequencia	= nr_seq_lote_conta_w;

if (coalesce(to_char(dt_geracao_analise_w),'X') <> 'X') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(262230);
	/* Mensagem: Não é possível desvincular o protocolo pois já foi iniciado o processo de análise no lote! */

end if;

update 	pls_protocolo_conta
set	nr_seq_lote_conta  = NULL
where	nr_sequencia = nr_seq_protocolo_p;

commit;

ds_historico_w	:= 'Desvinculado do lote '|| nr_seq_lote_conta_w;

insert into pls_prot_conta_hist(nr_sequencia,
	ds_historico,
	dt_atualizacao,
	dt_atualizacao_nrec,
	dt_historico,
	ie_tipo_historico,
	nm_usuario,
	nm_usuario_historico,
	nm_usuario_nrec,
	nr_seq_protocolo)
values (nextval('pls_prot_conta_hist_seq'),
	ds_historico_w,
	clock_timestamp(),
	clock_timestamp(),
	clock_timestamp(),
	'18',
	nm_usuario_p,
	nm_usuario_p,
	nm_usuario_p,
	nr_seq_protocolo_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
