CREATE OR REPLACE PROCEDURE pls_desvinc_titulos_lote_cam ( ds_lista_titulos_p text, nm_usuario_p text) AS $body$
DECLARE


ds_lista_titulos_w	varchar(2000);
nr_pos_virgula_w	bigint;
nr_titulo_w	bigint;
controle_w	smallint := 0;


BEGIN
if (ds_lista_titulos_p IS NOT NULL AND ds_lista_titulos_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	ds_lista_titulos_w := ds_lista_titulos_p;

	while(ds_lista_titulos_w IS NOT NULL AND ds_lista_titulos_w::text <> '') and (controle_w < 999) loop
		begin
		controle_w := controle_w + 1;

		nr_pos_virgula_w := position(',' in ds_lista_titulos_w);

		if (nr_pos_virgula_w IS NOT NULL AND nr_pos_virgula_w::text <> '') then
			begin

			nr_titulo_w		:= (substr(ds_lista_titulos_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_titulos_w	:= substr(ds_lista_titulos_w,nr_pos_virgula_w+1,length(ds_lista_titulos_w));

			end;
		else
			begin

			nr_pos_virgula_w	:= (ds_lista_titulos_w)::numeric;
			ds_lista_titulos_w	:= null;

			end;
		end if;

		if (nr_titulo_w > 0) then
			begin

			CALL pls_desvinc_tit_lote_camara(
				nr_titulo_w,
				nm_usuario_p,
				'N');

			end;
		end if;

		end;
	end loop;


	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
