CREATE OR REPLACE PROCEDURE pls_desvinc_titulo_cobranca ( nr_seq_cobranca_p bigint) AS $body$
DECLARE


nr_titulo_w		titulo_receber.nr_titulo%type;

c_titulo CURSOR FOR
	SELECT	a.nr_titulo
	from	titulo_receber_cobr	a
	where	a.nr_seq_cobranca	= nr_seq_cobranca_p;


BEGIN

open c_titulo;
loop
fetch c_titulo into
	nr_titulo_w;
EXIT WHEN NOT FOUND; /* apply on c_titulo */
	begin

	CALL desvincular_tit_rec_cobranca(nr_seq_cobranca_p, nr_titulo_w, 0, 'T');

	end;
end loop;
close c_titulo;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
