CREATE OR REPLACE PROCEDURE pls_desvinc_titulo_lote_pag ( nr_seq_pag_item_p bigint, nr_seq_lote_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_titulo_pag_w			bigint;
nr_titulo_rec_w			bigint;
nr_seq_movimento_w		bigint;
nr_seq_pagamento_w		bigint;
nr_adiant_pago_w		bigint;
qt_pag_item_w			integer;


BEGIN
if (nr_seq_pag_item_p IS NOT NULL AND nr_seq_pag_item_p::text <> '') then
	select	nr_tit_pagar_origem,
		nr_tit_receber_origem,
		nr_seq_pagamento,
		nr_adiant_pago_origem
	into STRICT	nr_titulo_pag_w,
		nr_titulo_rec_w,
		nr_seq_pagamento_w,
		nr_adiant_pago_w
	from	pls_pagamento_item
	where	nr_sequencia = nr_seq_pag_item_p;

	select	count(*)
	into STRICT	qt_pag_item_w
	from	pls_pagamento_item
	where	nr_seq_pagamento = nr_seq_pagamento_w;

	if (nr_titulo_pag_w IS NOT NULL AND nr_titulo_pag_w::text <> '') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_movimento_w
		from	pls_evento_movimento
		where	nr_tit_pagar_vinculado	= nr_titulo_pag_w;
	elsif (nr_titulo_rec_w IS NOT NULL AND nr_titulo_rec_w::text <> '') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_movimento_w
		from	pls_evento_movimento
		where	nr_tit_rec_vinculado	= nr_titulo_rec_w;
	elsif (nr_adiant_pago_w IS NOT NULL AND nr_adiant_pago_w::text <> '') then
		select	max(nr_sequencia)
		into STRICT	nr_seq_movimento_w
		from	pls_evento_movimento
		where	nr_adiant_pago	= nr_adiant_pago_w;
	end if;

	if (nr_seq_movimento_w IS NOT NULL AND nr_seq_movimento_w::text <> '') then
		delete from pls_evento_movimento
		where	nr_sequencia = nr_seq_movimento_w;
	end if;

	delete from pls_pagamento_item
	where	nr_sequencia = nr_seq_pag_item_p;

	if (qt_pag_item_w = 1) then
		delete from pls_pagamento_prestador
		where	nr_sequencia = nr_seq_pagamento_w;
	end if;

	CALL pls_atualizar_valor_lote_pag(nr_seq_pagamento_w,nm_usuario_p);
	pls_atualizar_eventos_pag_prod(nr_seq_lote_p);

	insert	into pls_pagamento_item_log(nr_sequencia,
		nr_titulo_pagar,
		nr_titulo_receber,
		nr_adiant_pago,
		nr_seq_pag_prest,
		nr_seq_lote_pagamento,
		ie_tipo,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec)
	values (nextval('pls_pagamento_item_log_seq'),
		nr_titulo_pag_w,
		nr_titulo_rec_w,
		nr_adiant_pago_w,
		nr_seq_pagamento_w,
		nr_seq_lote_p,
		'E',
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp());

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
