CREATE OR REPLACE PROCEDURE pls_desvinc_tit_lote_camara ( nr_seq_titulo_lote_p bigint, nm_usuario_p text, ie_commit_p text) AS $body$
DECLARE


nr_titulo_receber_w		bigint;
nr_titulo_pagar_w		bigint;
ie_tipo_inclusao_w		varchar(2);
nr_seq_lote_camara_w		bigint;
ie_commit_w			varchar(1);
nr_seq_camara_fatura_w		bigint;


BEGIN
if (nr_seq_titulo_lote_p IS NOT NULL AND nr_seq_titulo_lote_p::text <> '') then
	ie_commit_w := coalesce(ie_commit_p,'S');

	select	nr_titulo_receber,
		nr_titulo_pagar,
		ie_tipo_inclusao,
		nr_seq_lote_camara
	into STRICT	nr_titulo_receber_w,
		nr_titulo_pagar_w,
		ie_tipo_inclusao_w,
		nr_seq_lote_camara_w
	from	pls_titulo_lote_camara a
	where	a.nr_sequencia	= nr_seq_titulo_lote_p;

	delete from pls_titulo_lote_camara
	where	nr_sequencia = nr_seq_titulo_lote_p;

	if (nr_seq_lote_camara_w IS NOT NULL AND nr_seq_lote_camara_w::text <> '') and (nr_titulo_receber_w IS NOT NULL AND nr_titulo_receber_w::text <> '') then
		select	max(b.nr_sequencia)
		into STRICT	nr_seq_camara_fatura_w
		from	ptu_camara_compensacao a,
			ptu_camara_fatura b
		where	a.nr_sequencia		= b.nr_seq_camara
		and	b.nr_fatura		= nr_titulo_receber_w
		and	a.nr_seq_lote_camara	= nr_seq_lote_camara_w;

		if (nr_seq_camara_fatura_w IS NOT NULL AND nr_seq_camara_fatura_w::text <> '') then
			delete	FROM ptu_camara_fatura
			where	nr_sequencia = nr_seq_camara_fatura_w;
		end if;
	end if;

	/* Se o título havia sido incluído automaticamente deve gravar no log de exclusão */

	if (ie_tipo_inclusao_w = 'A') then
		insert into pls_lote_camara_comp_hist(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nr_seq_lote_camara,
			nr_titulo_receber,
			nr_titulo_pagar,
			ie_tipo_historico)
		values (nextval('pls_lote_camara_comp_hist_seq'),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_lote_camara_w,
			nr_titulo_receber_w,
			nr_titulo_pagar_w,
			'E');
	end if;

	if (ie_commit_w = 'S') then
		if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
