CREATE OR REPLACE PROCEDURE pls_desvinc_tit_notif_cobranca ( nr_seq_lote_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


C01 CURSOR FOR
	SELECT	a.nr_sequencia nr_seq_cobranca
	from	cobranca		a,
		pls_notificacao_item	b,
		pls_notificacao_pagador	c
	where	b.nr_sequencia	= a.nr_seq_notific_item
	and	c.nr_sequencia	= b.nr_seq_notific_pagador
	and	a.ie_status	<> 'C' --Não cancelar o que já estiver cancelado
	and	c.nr_seq_lote	= nr_seq_lote_p;

BEGIN

for r_c01_w in C01 loop
	begin
	update	cobranca
	set	dt_cancelamento		= clock_timestamp(),
		ie_status		= 'C',
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= r_c01_w.nr_seq_cobranca;
	end;
end loop;

update	pls_notificacao_pagador
set	nr_seq_regra_rat_cobr 	 = NULL
where	nr_seq_lote 		= nr_seq_lote_p;

update	pls_notificacao_lote
set	dt_geracao_cobranca	 = NULL,
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_lote_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
