CREATE OR REPLACE PROCEDURE pls_desvin_item_lote_apres ( nr_seq_item_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_guia_w		bigint;
nr_seq_lote_w		bigint;
qt_conta_w		integer;
qt_guia_w		integer;

BEGIN

select	max(nr_seq_guia),
	max(nr_seq_lote)
into STRICT	nr_seq_guia_w,
	nr_seq_lote_w
from	pls_apres_automatica_guia
where	nr_sequencia 	= nr_seq_item_p;

select	count(1)
into STRICT	qt_conta_w
from	pls_conta
where	nr_seq_guia	= nr_seq_guia_w
and	ie_status	!= 'C';

if (qt_conta_w	> 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(391581);
end if;

if (nr_seq_guia_w IS NOT NULL AND nr_seq_guia_w::text <> '') and (nr_seq_lote_w IS NOT NULL AND nr_seq_lote_w::text <> '') then
	insert into pls_lote_apres_aut_log(	nr_sequencia, nm_usuario, nm_usuario_nrec,
						dt_atualizacao, dt_atualizacao_nrec, nr_seq_guia,
						nr_seq_lote )
				values (	nextval('pls_lote_apres_aut_log_seq'), nm_usuario_p, nm_usuario_p,
						clock_timestamp(), clock_timestamp(), nr_seq_guia_w,
						nr_seq_lote_w);
	delete	FROM pls_apres_automatica_guia
	where	nr_sequencia = nr_seq_item_p;
end if;

select	count(1)
into STRICT	qt_guia_w
from	pls_apres_automatica_guia
where	nr_seq_lote = nr_seq_lote_w;

if (qt_guia_w = 0) then
	update	pls_lote_apres_automatica
	set	ie_status = '2'
	where	nr_sequencia = nr_seq_lote_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
