CREATE OR REPLACE PROCEDURE pls_desv_tit_lote_fat_lote ( nr_seq_lote_p pls_fatura.nr_seq_lote%type, nm_usuario_p usuario.nm_usuario%type, ie_commit_p text, ds_motivo_canc_p text, ie_origem_p text) AS $body$
DECLARE

					
C01 CURSOR(	nr_seq_lote_pc	pls_lote_pagamento.nr_sequencia%type) FOR
	SELECT	nr_sequencia,
		coalesce(pls_obter_dados_pls_fatura(nr_sequencia, 'SNF'), pls_obter_dados_pls_fatura(nr_sequencia, 'SNFN')) nr_seq_nota_fiscal
	from	pls_fatura
	where	nr_seq_lote = nr_seq_lote_pc
	and	coalesce(ie_cancelamento::text, '') = '';
	
BEGIN

for r_c01_w in c01( nr_seq_lote_p ) loop
	if (r_c01_w.nr_seq_nota_fiscal IS NOT NULL AND r_c01_w.nr_seq_nota_fiscal::text <> '') then
		CALL estornar_nota_fiscal(r_c01_w.nr_seq_nota_fiscal, nm_usuario_p, ie_commit_p, ds_motivo_canc_p, ie_origem_p);
	else
		CALL pls_desv_tit_lote_faturamento(r_c01_w.nr_sequencia, null, nm_usuario_p, 'N', null, ds_motivo_canc_p, ie_origem_p);
	end if;
end loop;

end;
$body$
