CREATE OR REPLACE PROCEDURE pls_devolver_fatura_tit_receb ( nr_seq_mot_devolucao_p bigint, nr_seq_fatura_p bigint, dt_devolucao_p timestamp, nm_usuario_p text) AS $body$
DECLARE

				 
nr_titulo_w 		bigint;
				

BEGIN 
if (nr_seq_mot_devolucao_p IS NOT NULL AND nr_seq_mot_devolucao_p::text <> '') and (dt_devolucao_p IS NOT NULL AND dt_devolucao_p::text <> '') and (nr_seq_fatura_p IS NOT NULL AND nr_seq_fatura_p::text <> '') then 
	select	max(nr_titulo) 
	into STRICT	nr_titulo_w 
	from	titulo_receber 
	where	nr_seq_ptu_fatura = nr_seq_fatura_p;
	 
	if (nr_titulo_w IS NOT NULL AND nr_titulo_w::text <> '') then 
		insert into titulo_receber_devolucao(nr_sequencia, 
			nr_seq_motivo_dev, 
			nr_titulo, 
			dt_devolucao, 
			nm_usuario, 
			dt_atualizacao) 
		values (nextval('titulo_receber_devolucao_seq'), 
			nr_seq_mot_devolucao_p, 
			nr_titulo_w, 
			dt_devolucao_p, 
			nm_usuario_p, 
			clock_timestamp());
		 
		update	titulo_receber 
		set	dt_ultima_devolucao	= dt_devolucao_p, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao 		= clock_timestamp() 
		where	nr_titulo		= nr_titulo_w;
		 
		update	ptu_fatura 
		set	nr_seq_mot_devolucao	= nr_seq_mot_devolucao_p, 
			nm_usuario_devolucao	= nm_usuario_p, 
			dt_devolucao		= dt_devolucao_p 
		where	nr_sequencia		= nr_seq_fatura_p;
		 
		CALL ptu_atualizar_status_fatura(nr_seq_fatura_p, 'R', null, nm_usuario_p);
		 
		commit;
	end if;	
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
