CREATE OR REPLACE PROCEDURE pls_dias_renovcao_carteira ( nr_seq_contrato_p bigint, cd_estabelecimento_p bigint, ie_commit_p text, nm_usuario_p text) AS $body$
DECLARE


qt_dias_valid_cart_w		bigint;


BEGIN

select	max(qt_dias_valid_cart)
into STRICT	qt_dias_valid_cart_w
from	pls_contrato
where	nr_sequencia	= nr_seq_contrato_p;

if (qt_dias_valid_cart_w > 0) then
	insert into pls_carteira_renovacao(	nr_sequencia, cd_estabelecimento, dt_atualizacao,
			nm_usuario, dt_atualizacao_nrec , nm_usuario_nrec,
			qt_meses_renovacao , nr_seq_contrato 	)
	values (	nextval('pls_carteira_renovacao_seq') , cd_estabelecimento_p ,  clock_timestamp() ,
			nm_usuario_p , clock_timestamp() , nm_usuario_p,
			 qt_dias_valid_cart_w, nr_seq_contrato_p	);
end if;

if (coalesce(ie_commit_p,'S') = 'S') then
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
