CREATE OR REPLACE PROCEDURE pls_diops_confirmar_envio ( nr_seq_periodo_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_maquina_w		varchar(80);


BEGIN

select	substr(obter_inf_sessao(0)||' - '||obter_inf_sessao(1),1,80)
into STRICT	ds_maquina_w
;

update	diops_periodo
set	nm_usuario_envio	= nm_usuario_p,
	ds_maquina_envio	= ds_maquina_w,
	dt_envio		= clock_timestamp(),
	nm_usuario		= nm_usuario_p,
	dt_atualizacao		= clock_timestamp()
where	nr_sequencia		= nr_seq_periodo_p;

CALL pls_diops_gravar_historico(nr_seq_periodo_p,4,'','',nm_usuario_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
