CREATE OR REPLACE PROCEDURE pls_dividir_dados_sca_perfil ( ds_dados_sca_p text, nr_seq_plano_sca_1_p INOUT bigint, nr_seq_tabela_sca_1_p INOUT bigint, nr_seq_plano_sca_2_p INOUT bigint, nr_seq_tabela_sca_2_p INOUT bigint, nr_seq_plano_sca_3_p INOUT bigint, nr_seq_tabela_sca_3_p INOUT bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Separar as sequências dos planos e tabelas registrados em uma lista.
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ ]  Objetos do dicionário [X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_dados_sca_w			varchar(4000);
nr_seq_plano_sca_w		bigint;
nr_seq_tabela_sca_w		bigint;
ie_dado_preenchido_w		bigint;


BEGIN
if (ds_dados_sca_p IS NOT NULL AND ds_dados_sca_p::text <> '') then
	ds_dados_sca_w 		:= ds_dados_sca_p;
	ie_dado_preenchido_w 	:= 1;

	/* Os dados são preenchidos da seguinte forma: '#'||nr_seq_plano_sca_w||';'||nr_seq_tabela_sca_w||';' */

	while(position('#' in ds_dados_sca_w) > 0) loop
		/* Obter a sequencia do plano SCA */

		nr_seq_plano_sca_w 	:= substr(ds_dados_sca_w, position('#' in ds_dados_sca_w) + 1, position(';' in ds_dados_sca_w) - 2);
		ds_dados_sca_w 		:= substr(ds_dados_sca_w, position(';' in ds_dados_sca_w) + 1, length(ds_dados_sca_w));

		/* Obter a sequencia da tabela SCA */

		nr_seq_tabela_sca_w 	:= substr(ds_dados_sca_w, 1, position(';' in ds_dados_sca_w) - 1);
		ds_dados_sca_w 		:= substr(ds_dados_sca_w, position(';' in ds_dados_sca_w) + 1, length(ds_dados_sca_w));

		if (ie_dado_preenchido_w = 1) then
			nr_seq_plano_sca_1_p 	:= nr_seq_plano_sca_w;
			nr_seq_tabela_sca_1_p	:= nr_seq_tabela_sca_w;
		elsif (ie_dado_preenchido_w = 2) then
			nr_seq_plano_sca_2_p 	:= nr_seq_plano_sca_w;
			nr_seq_tabela_sca_2_p	:= nr_seq_tabela_sca_w;
		elsif (ie_dado_preenchido_w = 3) then
			nr_seq_plano_sca_2_p 	:= nr_seq_plano_sca_w;
			nr_seq_tabela_sca_2_p	:= nr_seq_tabela_sca_w;
		end if;

		ie_dado_preenchido_w := ie_dado_preenchido_w + 1;
	end loop;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
