CREATE OR REPLACE PROCEDURE pls_dividir_lote_sib ( nr_seq_lote_p text, nm_usuario_p text) AS $body$
DECLARE


dt_mesano_referencia_w		pls_lote_retorno_sib.dt_mesano_referencia%type;
ie_tipo_lote_w			pls_lote_retorno_sib.ie_tipo_lote%type;
nr_seq_lote_ret_sib_w		pls_lote_retorno_sib.nr_sequencia%type;
cd_estabelecimento_w		pls_lote_retorno_sib.cd_estabelecimento%type;
qt_registros_w			bigint;
qt_lote_ww			bigint;
qt_lote_w			bigint;


BEGIN

select	dt_mesano_referencia,
	ie_tipo_lote,
	cd_estabelecimento
into STRICT	dt_mesano_referencia_w,
	ie_tipo_lote_w,
	cd_estabelecimento_w
from	pls_lote_retorno_sib
where	nr_sequencia = nr_seq_lote_p;

select	nextval('pls_lote_retorno_sib_seq')
into STRICT	nr_seq_lote_ret_sib_w
;

insert into pls_lote_retorno_sib(nr_sequencia, dt_mesano_referencia, ie_tipo_lote,
	dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
	nm_usuario_nrec, cd_estabelecimento)
values (nr_seq_lote_ret_sib_w, dt_mesano_referencia_w, ie_tipo_lote_w,
	clock_timestamp(), nm_usuario_p, clock_timestamp(),
	nm_usuario_p, cd_estabelecimento_w);

select	count(1)
into STRICT	qt_registros_w
from	pls_retorno_sib
where	nr_seq_lote_sib = nr_seq_lote_p;

qt_registros_w := qt_registros_w/2;

update	pls_retorno_sib
set	nr_seq_lote_sib = nr_seq_lote_ret_sib_w
where	nr_seq_lote_sib = nr_seq_lote_p
and	nr_sequencia in (SELECT	nr_Sequencia
			from 	pls_retorno_sib
			where 	nr_seq_Lote_sib = nr_seq_lote_p  LIMIT (qt_registros_w));

select	count(1)
into STRICT	qt_lote_ww
from	pls_retorno_sib
where	nr_seq_lote_sib = nr_seq_lote_p;

update	pls_lote_retorno_sib
set	qt_registros_lote = qt_lote_ww
where	nr_sequencia = nr_seq_lote_p;

select	count(1)
into STRICT	qt_lote_w
from	pls_retorno_sib
where	nr_seq_lote_sib = nr_seq_lote_ret_sib_w;

update	pls_lote_retorno_sib
set	qt_registros_lote = qt_lote_w
where	nr_sequencia = nr_seq_lote_ret_sib_w;

commit;

end;
$body$
