CREATE OR REPLACE PROCEDURE pls_duplicar_instr_juridico ( nr_seq_instr_juridico_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_plano_instr_w		bigint;
nr_seq_plano_instrumento_w	bigint;
nr_seq_instrumento_w		bigint;
nr_seq_intrumento_tema_w	bigint;
ds_tema_w			varchar(32764);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_plano_instrumento
	where	nr_seq_instrumento	= nr_seq_instr_juridico_p
	order by	nr_sequencia;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_instrumento_tema
	where	nr_seq_instrumento	= nr_seq_plano_instr_w
	order by	nr_sequencia;


BEGIN

select	nextval('pls_instrumento_seq')
into STRICT	nr_seq_instrumento_w
;

insert	into	pls_instrumento(	nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_instrumento,
		nr_seq_plano,
		ie_situacao,
		dt_inicio_vigencia,
		dt_fim_vigencia,
		ds_observacao)
	(SELECT	nr_seq_instrumento_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		'CÃ³pia de ' || ds_instrumento,
		nr_seq_plano,
		ie_situacao,
		dt_inicio_vigencia,
		dt_fim_vigencia,
		ds_observacao
	from	pls_instrumento
	where	nr_sequencia	= nr_seq_instr_juridico_p);

insert	into	pls_instrumento_guia(	nr_sequencia,
		nr_seq_instrumento,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_titulo,
		nr_pagina,
		ds_guia_leitura)
	(SELECT	nextval('pls_instrumento_guia_seq'),
		nr_seq_instrumento_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_titulo,
		nr_pagina,
		ds_guia_leitura
	from	pls_instrumento_guia
	where	nr_seq_instrumento	= nr_seq_instr_juridico_p);

open C01;
loop
fetch C01 into
	nr_seq_plano_instr_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	ds_tema
	into STRICT	ds_tema_w
	from	pls_plano_instrumento
	where	nr_sequencia	= nr_seq_plano_instr_w;

	select	nextval('pls_plano_instrumento_seq')
	into STRICT	nr_seq_plano_instrumento_w
	;

	insert	into	pls_plano_instrumento(	nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			--ds_tema,
			nm_dispositivo,
			nr_seq_instrumento,
			ie_gerar_titulo,
			cd_classificacao,
			ie_permite_inclusao_termo,
			ie_quebra_pagina)
		(SELECT	nr_seq_plano_instrumento_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			--ds_tema_w,
			nm_dispositivo,
			nr_seq_instrumento_w,
			ie_gerar_titulo,
			cd_classificacao,
			ie_permite_inclusao_termo,
			ie_quebra_pagina
		from	pls_plano_instrumento
		where	nr_sequencia	= nr_seq_plano_instr_w);

	update	pls_plano_instrumento
	set	ds_tema	= ds_tema_w
	where	nr_sequencia	= nr_seq_plano_instrumento_w;

	open C02;
	loop
	fetch C02 into
		nr_seq_intrumento_tema_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		insert	into	pls_instrumento_tema(	nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_tema,
				nr_seq_instrumento)
			(SELECT	nextval('pls_instrumento_tema_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_tema,
				nr_seq_plano_instrumento_w
			from	pls_instrumento_tema
			where	nr_sequencia	= nr_seq_intrumento_tema_w);
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
