CREATE OR REPLACE PROCEDURE pls_duplicar_regras_estab ( nr_seq_regra_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nm_coluna_w			varchar(255);
ds_colunas_w			varchar(4000);
nr_seq_regra_nova_w		pls_conversao_proc.nr_sequencia%type;
ds_insert_w			varchar(4000);



c_coluna_regra CURSOR FOR
	SELECT 	column_name
	from 	user_tab_columns
	where 	table_name = 'PLS_CONVERSAO_PROC'
	and	column_name not in ('NR_SEQUENCIA','NM_USUARIO','NM_USUARIO_NREC','DT_ATUALIZACAO','DT_ATUALIZACAO_NREC','CD_ESTABELECIMENTO');


BEGIN

open c_coluna_regra;
loop
fetch c_coluna_regra into	
	nm_coluna_w;
EXIT WHEN NOT FOUND; /* apply on c_coluna_regra */
	begin
		
	ds_colunas_w	:= ds_colunas_w || ', ' || nm_coluna_w;
	
	end;
end loop;
close c_coluna_regra;


select 	nextval('pls_conversao_proc_seq')
into STRICT 	nr_seq_regra_nova_w
;

ds_insert_w	:=  'insert into pls_conversao_proc(	nr_sequencia, 
							nm_usuario, 
							nm_usuario_nrec, 
							dt_atualizacao, 
							dt_atualizacao_nrec,
							cd_estabelecimento'
							|| ds_colunas_w || ') 
						(	select 	' || nr_seq_regra_nova_w || ',' || 
							'''' 	|| nm_usuario_p || ''','
							|| '''' || nm_usuario_p || '''' ||
							', sysdate, 
							sysdate,' ||
							cd_estabelecimento_p
							|| ds_colunas_w || 	
						' from 	pls_conversao_proc where nr_sequencia =' || nr_seq_regra_p || ')';

CALL exec_sql_dinamico('Tasy', ds_insert_w);
						
commit;

end;
$body$
