CREATE OR REPLACE PROCEDURE pls_duplicar_regra_acrescimo ( nr_seq_contrato_p pls_contrato.nr_sequencia%type, nr_seq_segurado_p pls_segurado.nr_sequencia%type, nr_seq_regra_p pls_regra_acrescimo.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p usuario.cd_estabelecimento%type) AS $body$
DECLARE

				
qt_min_vidas_w			pls_criterio_acrescimo.qt_min_vidas%type;
qt_max_vidas_w			pls_criterio_acrescimo.qt_max_vidas%type;
tx_acrescimo_w			pls_criterio_acrescimo.tx_acrescimo%type;
vl_acrescimo_w			pls_criterio_acrescimo.vl_acrescimo%type;
nr_seq_regra_acres_w		pls_regra_acrescimo.nr_sequencia%type;
ie_tipo_segurado_w		pls_criterio_acrescimo.ie_tipo_segurado%type;
dt_inicio_vigencia_w		pls_regra_acrescimo.dt_inicio_vigencia%type;

C01 CURSOR FOR
	SELECT	qt_min_vidas,
		qt_max_vidas,
		tx_acrescimo,
		vl_acrescimo,
		ie_tipo_segurado
	from 	pls_criterio_acrescimo
	where	nr_seq_regra = nr_seq_regra_p
	order by nr_sequencia;


BEGIN

if (coalesce(nr_seq_contrato_p,0) > 0) then
	select 	dt_contrato
	into STRICT	dt_inicio_vigencia_w
	from	pls_contrato
	where	nr_sequencia = nr_seq_contrato_p;
elsif (coalesce(nr_seq_segurado_p,0) > 0) then
	select	coalesce(dt_contratacao,clock_timestamp())
	into STRICT	dt_inicio_vigencia_w
	from	pls_segurado
	where	nr_sequencia	= nr_seq_segurado_p;
end if;

select 	nextval('pls_regra_acrescimo_seq')
into STRICT 	nr_seq_regra_acres_w
;

insert into pls_regra_acrescimo(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ds_regra,
	ie_situacao,
	ie_tipo_regra,
	cd_estabelecimento,
	dt_fim_vigencia)
SELECT	nr_seq_regra_acres_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	ds_regra,
	'A',
	ie_tipo_regra,
	cd_estabelecimento_p,
	null
from	pls_regra_acrescimo
where 	nr_sequencia	= nr_seq_regra_p;

if (coalesce(nr_seq_contrato_p,0) > 0) then
	update 	pls_regra_acrescimo
	set	nr_seq_contrato	= nr_seq_contrato_p
	where 	nr_sequencia	= nr_seq_regra_acres_w;
elsif (coalesce(nr_seq_segurado_p,0) > 0) then
	update 	pls_regra_acrescimo
	set	nr_seq_segurado	= nr_seq_segurado_p
	where 	nr_sequencia	= nr_seq_regra_acres_w;
end if;

open C01;
loop
fetch C01 into	
	qt_min_vidas_w,
	qt_max_vidas_w,
	tx_acrescimo_w,
	vl_acrescimo_w,
	ie_tipo_segurado_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	insert into pls_criterio_acrescimo(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		qt_min_vidas,
		qt_max_vidas,
		nr_seq_regra,
		tx_acrescimo,
		vl_acrescimo,
		ie_tipo_segurado,
		cd_estabelecimento)
	values (	nextval('pls_criterio_acrescimo_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		qt_min_vidas_w,
		qt_max_vidas_w,
		nr_seq_regra_acres_w,
		tx_acrescimo_w,
		vl_acrescimo_w,
		ie_tipo_segurado_w,
		cd_estabelecimento_p);
	end;
end loop;
close C01;


if (coalesce(nr_seq_contrato_p,0) > 0) then
	update	pls_regra_acrescimo
	set	dt_inicio_vigencia	= dt_inicio_vigencia_w
	where	nr_sequencia		= nr_seq_regra_acres_w;
elsif (coalesce(nr_seq_segurado_p,0) > 0) then
	update	pls_regra_acrescimo
	set	dt_inicio_vigencia	= dt_inicio_vigencia_w
	where	nr_sequencia		= nr_seq_regra_acres_w;
end if;

commit;
end;
$body$
