CREATE OR REPLACE PROCEDURE pls_duplicar_regra_desconto ( nr_seq_contrato_p bigint, nr_seq_proposta_adesao_p bigint, nr_seq_regra_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


qt_min_vidas_w			bigint;
qt_max_vidas_w			bigint;
tx_desconto_w			double precision;
nr_seq_regra_desc_w		bigint;
dt_inicio_vigencia_w		timestamp;
ie_tipo_segurado_w		varchar(1);
vl_desconto_w			double precision;

C01 CURSOR FOR
	SELECT	qt_min_vidas,
		qt_max_vidas,
		tx_desconto,
		ie_tipo_segurado,
		vl_desconto
	from 	pls_preco_regra
	where	nr_seq_regra = nr_seq_regra_p
	order by nr_sequencia;


BEGIN

if (coalesce(nr_seq_contrato_p,0) > 0) then
	select 	dt_contrato
	into STRICT	dt_inicio_vigencia_w
	from	pls_contrato
	where	nr_sequencia = nr_seq_contrato_p;
elsif (coalesce(nr_seq_proposta_adesao_p,0) > 0) then
	select	coalesce(dt_inicio_proposta,clock_timestamp())
	into STRICT	dt_inicio_vigencia_w
	from	pls_proposta_adesao
	where	nr_sequencia	= nr_seq_proposta_adesao_p;
end if;

select 	nextval('pls_regra_desconto_seq')
into STRICT 	nr_seq_regra_desc_w
;

insert into pls_regra_desconto(
	nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	dt_atualizacao_nrec,
	nm_usuario_nrec,
	ds_regra,
	nr_seq_plano,
	nr_seq_contrato,
	ie_situacao,
	dt_inicio_vigencia,
	dt_fim_vigencia,
	ie_tipo_regra,
	nr_seq_proposta,
	cd_estabelecimento)
SELECT	nr_seq_regra_desc_w,
	clock_timestamp(),
	nm_usuario_p,
	clock_timestamp(),
	nm_usuario_p,
	ds_regra,
	null,
	nr_seq_contrato_p,
	'A',
	dt_inicio_vigencia_w,
	null,
	ie_tipo_regra,
	nr_seq_proposta_adesao_p,
	cd_estabelecimento_p
from	pls_regra_desconto
where 	nr_sequencia	= nr_seq_regra_p;

open C01;
loop
fetch C01 into
	qt_min_vidas_w,
	qt_max_vidas_w,
	tx_desconto_w,
	ie_tipo_segurado_w,
	vl_desconto_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	insert into pls_preco_regra(
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		qt_min_vidas,
		qt_max_vidas,
		nr_seq_regra,
		tx_desconto,
		ie_tipo_segurado,
		vl_desconto)
	values (	nextval('pls_preco_regra_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		qt_min_vidas_w,
		qt_max_vidas_w,
		nr_seq_regra_desc_w,
		tx_desconto_w,
		ie_tipo_segurado_w,
		vl_desconto_w);
end loop;
close C01;

commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
