CREATE OR REPLACE PROCEDURE pls_duplicar_regra_precos ( ds_lista_sequencia_p text, ie_tipo_regra_p text, dt_inicio_p timestamp, ie_checado_p text, nm_usuario_p text) AS $body$
DECLARE

 
ds_lista_sequencia_w	varchar(2000);
nr_pos_virgula_w	bigint;
nr_sequencia_w		bigint;
dt_inicio_w		timestamp;

BEGIN
if (ds_lista_sequencia_p IS NOT NULL AND ds_lista_sequencia_p::text <> '') and (ie_tipo_regra_p IS NOT NULL AND ie_tipo_regra_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
 
	ds_lista_sequencia_w := ds_lista_sequencia_p;
 
	while(ds_lista_sequencia_w IS NOT NULL AND ds_lista_sequencia_w::text <> '') loop 
		begin 
		nr_pos_virgula_w	:= position(',' in ds_lista_sequencia_w);
 
		if (nr_pos_virgula_w > 0) then 
			begin 
			nr_sequencia_w		:= (substr(ds_lista_sequencia_w,0,nr_pos_virgula_w-1))::numeric;
			ds_lista_sequencia_w	:= substr(ds_lista_sequencia_w,nr_pos_virgula_w+1,length(ds_lista_sequencia_w));
			end;
		else 
			begin 
			nr_sequencia_w		:= (ds_lista_sequencia_w)::numeric;
			ds_lista_sequencia_w	:= null;
			end;
		end if;
 
		if (nr_sequencia_w > 0) then 
			CALL pls_duplicar_regra_preco(nr_sequencia_w, 
						 dt_inicio_p, 
						 ie_tipo_regra_p, 
					 	 nm_usuario_p, 
						 ie_checado_p, 
						 'N');
		end if;
		end;
	end loop;
	end;
end if;
 
commit;
 
end;
$body$
