CREATE OR REPLACE PROCEDURE pls_duplicar_tabela_base (nr_seq_tabela_p bigint, nr_seq_contrato_p bigint, nm_usuario_p text, nr_seq_tabela_nova_p INOUT bigint) AS $body$
DECLARE


nr_seq_tabela_nova_w			bigint;
nr_seq_plano_w			bigint;
qt_idade_inicial_w			bigint;
qt_idade_final_w			bigint;
vl_preco_atual_w			double precision;
tx_acrescimo_w				double precision;
ie_tabela_contrato_w			varchar(20);

c01 CURSOR FOR
SELECT	qt_idade_inicial,
	qt_idade_final,
	vl_preco_atual,
	tx_acrescimo
from	pls_plano_preco
where	nr_seq_tabela	= nr_seq_tabela_p
order	by qt_idade_inicial;


BEGIN

select	nr_seq_plano
into STRICT	nr_seq_plano_w
from	pls_tabela_preco
where	nr_sequencia	= nr_seq_tabela_p;

/*if	(ie_tabela_contrato_w = 'N') then
	 r aise_application_error(-20011,'A tabela informada não é tabela base por contrato. Verifique!');
end if;*/
select	nextval('pls_tabela_preco_seq')
into STRICT	nr_seq_tabela_nova_w
;

insert into pls_tabela_preco(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	nm_tabela,
	dt_inicio_vigencia,
	dt_fim_vigencia,
	nr_seq_plano,
	dt_liberacao,
	ie_tabela_base,
	nr_contrato,
	nr_seq_tabela_origem,
	nr_seq_faixa_etaria)
SELECT	nr_seq_tabela_nova_w,
	clock_timestamp(),
	nm_usuario_p,
	nm_tabela || ' - Cópia',
	dt_inicio_vigencia,
	clock_timestamp(),
	nr_seq_plano_w,
	clock_timestamp(),
	'N',
	nr_seq_contrato_p,
	nr_sequencia,
	nr_seq_faixa_etaria
from	pls_tabela_preco
where	nr_sequencia	= nr_seq_tabela_p;

open c01;
loop
fetch c01 into
	qt_idade_inicial_w,
	qt_idade_final_w,
	vl_preco_atual_w,
	tx_acrescimo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	insert into pls_plano_preco(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nr_seq_tabela,
		qt_idade_inicial,
		qt_idade_final,
		vl_preco_inicial,
		vl_preco_atual,
		tx_acrescimo)
	values (nextval('pls_plano_preco_seq'),
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_tabela_nova_w,
		qt_idade_inicial_w,
		qt_idade_final_w,
		vl_preco_atual_w,
		vl_preco_atual_w,
		tx_acrescimo_w);
end loop;
close c01;

nr_seq_tabela_nova_p		:= nr_seq_tabela_nova_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
