SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_duplicar_tabela_preco (nr_seq_tabela_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_tabela_nova_w		bigint;
qt_faixa_etaria_w		bigint;
nr_seq_plano_w			bigint;
dt_inicio_vigencia_w		timestamp;
ie_tabela_base_w		varchar(1);
cd_codigo_ant_w			varchar(20);
nm_tabela_w			varchar(255);
vl_preco_atual_w		double precision;
qt_idade_inicial_w		integer;
qt_idade_final_w		integer;
nr_seq_preco_w			bigint;
nr_seq_sca_w			bigint;
ie_grau_titularidade_w		varchar(2);
nr_seq_faixa_etaria_w		bigint;
qt_vidas_inicial_w		pls_plano_preco.qt_vidas_inicial%type;
qt_vidas_final_w		pls_plano_preco.qt_vidas_final%type;
tx_acrescimo_w 			double precision;
ie_proposta_adesao_w      	varchar(2);
ie_preco_vidas_contrato_w 	varchar(2);
ie_calculo_vidas_w        	varchar(2);
nr_seq_principal_w		bigint;
tx_reajuste_w			double precision;

c01 CURSOR FOR
	SELECT	nr_sequencia,
		qt_idade_inicial,
		qt_idade_final,
		vl_preco_atual,
		ie_grau_titularidade,
		tx_acrescimo,
		qt_vidas_inicial,
		qt_vidas_final,
		tx_reajuste
	from	pls_plano_preco
	where	nr_seq_tabela	= nr_seq_tabela_p
		order by qt_vidas_inicial, qt_idade_inicial;


BEGIN
	select	ie_tabela_base,
		nm_tabela,
		dt_inicio_vigencia,
		cd_codigo_ant,
		nr_seq_plano,
		nr_seq_sca,
		nr_seq_faixa_etaria,
		ie_proposta_adesao,
		ie_preco_vidas_contrato,
		ie_calculo_vidas,
		nr_seq_principal
	into STRICT	ie_tabela_base_w,
		nm_tabela_w,
		dt_inicio_vigencia_w,
		cd_codigo_ant_w,
		nr_seq_plano_w,
		nr_seq_sca_w,
		nr_seq_faixa_etaria_w,
		ie_proposta_adesao_w,
		ie_preco_vidas_contrato_w,
		ie_calculo_vidas_w,
		nr_seq_principal_w
		from	pls_tabela_preco
	where	nr_sequencia = nr_seq_tabela_p;

	if (ie_tabela_base_w = 'S') then
		--r aise_application_error(-20011,'A tabela é base por percentual, não pode ser duplicada!');
		CALL wheb_mensagem_pck.exibir_mensagem_abort(249955);
	end if;

	select	nextval('pls_tabela_preco_seq')
		into STRICT	nr_seq_tabela_nova_w
		;

	insert into pls_tabela_preco(nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		nm_tabela,
		dt_inicio_vigencia,
		nr_seq_plano,
		cd_codigo_ant,
		ie_tabela_base,
		nr_seq_sca,
		nr_seq_faixa_etaria,
		ie_proposta_adesao,
		ie_preco_vidas_contrato,
		ie_calculo_vidas,
		nr_seq_principal)
	values (nr_seq_tabela_nova_w,
		clock_timestamp(),
		nm_usuario_p,
		substr('Cópia de ' || nm_tabela_w, 1,255),
		dt_inicio_vigencia_w,
		nr_seq_plano_w,
		cd_codigo_ant_w,
		'N',
		nr_seq_sca_w,
		nr_seq_faixa_etaria_w,
		ie_proposta_adesao_w,
		ie_preco_vidas_contrato_w,
		ie_calculo_vidas_w,
		nr_seq_principal_w);

	select	count(*)
		into STRICT	qt_faixa_etaria_w
		from	pls_plano_preco
		where	nr_seq_tabela	= nr_seq_tabela_p;

	if (qt_faixa_etaria_w > 0) then
	
		open c01;
			loop
			fetch c01 into	nr_seq_preco_w,
			qt_idade_inicial_w,
			qt_idade_final_w,
			vl_preco_atual_w,
			ie_grau_titularidade_w,
			tx_acrescimo_w,
			qt_vidas_inicial_w,
			qt_vidas_final_w,
			tx_reajuste_w;			
			EXIT WHEN NOT FOUND; /* apply on c01 */

			insert into pls_plano_preco(nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				nr_seq_tabela,
				qt_idade_inicial,
				qt_idade_final,
				vl_preco_inicial,
				vl_preco_atual,
				tx_acrescimo,
				ie_grau_titularidade,
				qt_vidas_inicial,
				qt_vidas_final,
				tx_reajuste)
			values (nextval('pls_plano_preco_seq'),
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_tabela_nova_w,
				qt_idade_inicial_w,
				qt_idade_final_w,
				vl_preco_atual_w,
				vl_preco_atual_w,
				tx_acrescimo_w,
				ie_grau_titularidade_w,
				qt_vidas_inicial_w,
				qt_vidas_final_w,
				tx_reajuste_w);
			end loop;
		close c01;
		
	end if;

	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_duplicar_tabela_preco (nr_seq_tabela_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

