SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_duplicar_tab_contr_interc ( nr_seq_intercambio_p bigint, nr_seq_plano_p bigint, nr_seq_tabela_p bigint, nr_seq_tabela_nova_p INOUT bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
dt_contrato_w			timestamp;
nr_seq_tabela_w			bigint;
qt_idade_inicial_w		integer;
qt_idade_final_w		integer;
vl_preco_atual_w		double precision;
tx_acrescimo_w			double precision;
qt_registros_w			bigint;
ie_tabela_contrato_w		varchar(1);
nm_estipulante_w		varchar(80);
ie_tipo_contratacao_w		varchar(3);
vl_preco_nao_subsidiado_w	double precision;
ie_preco_w			varchar(2);
nr_seq_contrato_tabela_w	bigint;
nr_seq_intercambio_w		bigint;
vl_minimo_w			double precision;
ie_grau_titularidade_w		varchar(2);
IE_TIPO_REPASSE_w		varchar(10);

c01 CURSOR FOR 
	SELECT	qt_idade_inicial, 
		qt_idade_final, 
		vl_preco_atual, 
		tx_acrescimo, 
		vl_preco_nao_subsidiado, 
		vl_minimo, 
		ie_grau_titularidade 
	from	pls_plano_preco 
	where	nr_seq_tabela	= nr_seq_tabela_p 
	order by qt_idade_inicial;


BEGIN 
 
begin 
select	max(IE_TIPO_REPASSE) 
into STRICT	IE_TIPO_REPASSE_w 
from	pls_segurado 
where	nr_seq_intercambio	= nr_seq_intercambio_p;
exception 
when others then 
	IE_TIPO_REPASSE_w	:= 'X';
end;
 
if (IE_TIPO_REPASSE_w = 'P') then 
	select	nr_sequencia, 
		coalesce(dt_inclusao,clock_timestamp()), 
		substr(obter_nome_pf_pj(cd_pessoa_fisica,cd_cgc),1,80) 
	into STRICT	nr_seq_intercambio_w, 
		dt_contrato_w, 
		nm_estipulante_w 
	from	pls_intercambio 
	where	nr_sequencia	= nr_seq_intercambio_p;
	 
	if (nr_seq_plano_p IS NOT NULL AND nr_seq_plano_p::text <> '') and (nr_seq_tabela_p IS NOT NULL AND nr_seq_tabela_p::text <> '') then 
		 
		select	nextval('pls_tabela_preco_seq') 
		into STRICT	nr_seq_tabela_w 
		;
	 
		insert into pls_tabela_preco(nr_sequencia, 
			dt_atualizacao, 
			nm_usuario, 
			nm_tabela, 
			dt_inicio_vigencia, 
			nr_seq_plano, 
			ie_tabela_base, 
			NR_SEQ_CONTRATO_INTER, 
			nr_seq_tabela_origem, 
			nr_seq_faixa_etaria) 
		SELECT	nr_seq_tabela_w, 
			clock_timestamp(), 
			nm_usuario_p, 
			'Tabela para o contrato ' || to_char(nr_seq_intercambio_w) || ' - ' || nm_estipulante_w || ' - 2 ', 
			dt_contrato_w, 
			nr_seq_plano_p, 
			'N', 
			nr_seq_intercambio_p, 
			nr_sequencia, 
			nr_seq_faixa_etaria 
		from	pls_tabela_preco 
		where	nr_sequencia	= nr_seq_tabela_p;
		 
		open c01;
		loop 
		fetch c01 into	qt_idade_inicial_w, 
				qt_idade_final_w, 
				vl_preco_atual_w, 
				tx_acrescimo_w, 
				vl_preco_nao_subsidiado_w, 
				vl_minimo_w, 
				ie_grau_titularidade_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			 
			insert into pls_plano_preco(nr_sequencia, 
				dt_atualizacao, 
				nm_usuario, 
				nr_seq_tabela, 
				qt_idade_inicial, 
				qt_idade_final, 
				vl_preco_inicial, 
				vl_preco_atual, 
				tx_acrescimo, 
				vl_preco_nao_subsidiado, 
				vl_minimo, 
				ie_grau_titularidade) 
			values (nextval('pls_plano_preco_seq'), 
				clock_timestamp(), 
				nm_usuario_p, 
				nr_seq_tabela_w, 
				qt_idade_inicial_w, 
				qt_idade_final_w, 
				vl_preco_atual_w, 
				vl_preco_atual_w, 
				tx_acrescimo_w, 
				vl_preco_nao_subsidiado_w, 
				vl_minimo_w, 
				ie_grau_titularidade_w);
		end loop;
		close c01;
		 
		update	pls_intercambio_plano 
		set	nr_seq_tabela		= nr_seq_tabela_w, 
			nm_usuario		= nm_usuario_p, 
			dt_atualizacao		= clock_timestamp() 
		where	nr_seq_intercambio	= nr_seq_intercambio_p 
		and	nr_seq_tabela		= nr_seq_tabela_p 
		and	nr_seq_plano		= nr_seq_plano_p;
		 
		update	pls_tabela_preco 
		set	dt_fim_vigencia	= clock_timestamp() 
		where	nr_sequencia	= nr_seq_tabela_p;
		 
		insert into pls_contrato_historico(	nr_sequencia, 
					cd_estabelecimento, 
					nr_seq_intercambio, 
					dt_historico, 
					ie_tipo_historico, 
					dt_atualizacao, 
					nm_usuario, 
					dt_atualizacao_nrec, 
					nm_usuario_nrec, 
					ds_historico) 
				values (	nextval('pls_contrato_historico_seq'), 
					cd_estabelecimento_p, 
					nr_seq_intercambio_p, 
					clock_timestamp(), 
					3, 
					clock_timestamp(), 
					nm_usuario_p, 
					clock_timestamp(), 
					nm_usuario_p, 
					'Alteração de tabela de preço do contrato');
		 
	end if;
end if;
 
commit;
 
nr_seq_tabela_nova_p	:= nr_seq_tabela_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_duplicar_tab_contr_interc ( nr_seq_intercambio_p bigint, nr_seq_plano_p bigint, nr_seq_tabela_p bigint, nr_seq_tabela_nova_p INOUT bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

