CREATE OR REPLACE PROCEDURE pls_duplic_todas_regra_aut_exe ( nr_seq_ocorrencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_regra_w			bigint;
ie_origem_w			varchar(3);

C01 CURSOR FOR
	SELECT	nr_sequencia,
		'A' ie_origem
	from	pls_ocorrencia_regra
	where	nr_seq_ocorrencia = nr_seq_ocorrencia_p
	
union

	SELECT	nr_sequencia,
		'AI' ie_origem
	from	pls_ocorrencia_regra_item
	where	nr_seq_ocorrencia = nr_seq_ocorrencia_p
	
union

	select	nr_sequencia,
		'AE' ie_origem
	from	pls_excecao_ocorrencia
	where	nr_seq_ocorrencia = nr_seq_ocorrencia_p
	order by 1;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_w,
	ie_origem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	CALL pls_duplicar_regra_oc_aut_exec(nr_seq_regra_w,ie_origem_w,nm_usuario_p);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
