CREATE OR REPLACE PROCEDURE pls_encerrar_cobr_titulo_inad (nr_titulo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_historico_w			varchar(255);
nr_seq_hist_cob_liq_w		bigint;
nr_seq_cobranca_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	titulo_receber b,
		cobranca a
	where	a.nr_titulo		= b.nr_titulo
	and	a.nr_titulo		= nr_titulo_p
	and	a.ie_status		<> 'A';


BEGIN

select	nr_seq_hist_cob
into STRICT	nr_seq_hist_cob_liq_w
from	parametro_contas_receber
where	cd_estabelecimento	= cd_estabelecimento_p;

open C01;
loop
fetch C01 into
	nr_seq_cobranca_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ds_historico_w		:= 'Cobrança encerrada de forma automática pelo sistema devido ao título ter sido liquidado.' || chr(13) || chr(10) ||
			'Cobrança escritural: ' || nr_seq_cobranca_w;

	insert into cobranca_historico(nr_sequencia,
		nr_seq_cobranca,
		dt_atualizacao,
		nm_usuario,
		nr_seq_historico,
		vl_historico,
		ds_historico,
		dt_historico)
	values (nextval('cobranca_historico_seq'),
		nr_seq_cobranca_w,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_hist_cob_liq_w,
		0,
		ds_historico_w,
		clock_timestamp());

	update	cobranca_orgao
	set	dt_retirada	= clock_timestamp(),
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_seq_cobranca	= nr_seq_cobranca_w
	and	coalesce(dt_retirada::text, '') = '';

	update	cobranca
	set	ie_status	= 'E',
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_cobranca_w;
	end;
end loop;
close C01;

--commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_encerrar_cobr_titulo_inad (nr_titulo_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

