CREATE OR REPLACE PROCEDURE pls_encerrar_processo ( nr_seq_processo_p pls_processo.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


cont_w		bigint;


BEGIN

select	count(*)
into STRICT	cont_w
from	pls_processo_conta
where	nr_seq_processo	= nr_seq_processo_p
and	coalesce(ie_status_conta, '-1')	not in ('D','I', 'L');	-- D = Encerrada com deferimento, I = Encerrada com indeferimento, A - Acatada
if (cont_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(307267);	-- Existem contas n√£o encerradas neste processo!
end if;

update	pls_processo
set	ie_status	= 'E'
where	nr_sequencia	= nr_seq_processo_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
