CREATE OR REPLACE PROCEDURE pls_encerrar_vigencia ( nr_seq_pagador_p bigint, dt_fim_vigencia_p timestamp, nr_seq_cont_paga_fin_p bigint, nm_usuario_p text) AS $body$
BEGIN
	update	pls_contrato_pagador_fin
	set	dt_fim_vigencia		= fim_dia(dt_fim_vigencia_p),
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		nm_usuario_fim_vigencia	= nm_usuario_p
	where	nr_seq_pagador		= nr_seq_pagador_p
	and	nr_sequencia		= nr_seq_cont_paga_fin_p;

commit;

end;
$body$
