CREATE OR REPLACE PROCEDURE pls_enviar_cta_anexo_ptu ( nr_seq_anexo_p pls_conta_anexo.nr_sequencia%type, ie_ptu_a500_p pls_conta_anexo.ie_ptu_a500%type, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE

qt_extensao_w	integer;

BEGIN

-- Caso for envio, verifica se a extensão é compativel
if (ie_ptu_a500_p = 'S') then

	select	count(1)
	into STRICT	qt_extensao_w
	from	pls_conta_anexo	a
	where	a.nr_sequencia	= nr_seq_anexo_p
	and	upper(substr(a.ds_anexo, instr(a.ds_anexo, '.', -1) + 1, length(a.ds_anexo))) in (	SELECT	x.vl_dominio
													from	valor_dominio	x
													where	x.cd_dominio	= 8878);

	-- se não possuir a extensão válida pelo PTU
	if (qt_extensao_w = 0) then

		CALL wheb_mensagem_pck.exibir_mensagem_abort(1028544);
	end if;

end if;

update	pls_conta_anexo
set	ie_ptu_a500	= ie_ptu_a500_p,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_seq_anexo_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
