SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_enviar_lista_prest_email ( nr_seq_aut_email_p bigint, ds_email_dest_p text, nm_usuario_p text) AS $body$
DECLARE


ds_email_remetente_w		varchar(150) := '';
ds_assunto_w			varchar(255);
ds_texto_email_w		varchar(4000);


BEGIN



begin
	select	ds_assunto,
		ds_email,
		ds_email_remetente
	into STRICT	ds_assunto_w,
		ds_texto_email_w,
		ds_email_remetente_w
	from	pls_autorizacao_email
	where	nr_sequencia	= nr_seq_aut_email_p;
exception
when others then
	ds_assunto_w		:= '';
	ds_texto_email_w	:= '';
end;

if (coalesce(ds_email_remetente_w,'X') = 'X') then
	/* Obter o valor do parâmetro 52 - E-mail utilizado para o envio das mensagens pela autorização */

	ds_email_remetente_w	:= coalesce(obter_valor_param_usuario(1204, 52, Obter_Perfil_Ativo, nm_usuario_p, 0), 'X');
end if;

if (coalesce(ds_email_dest_p,'X') <> 'X') and (coalesce(ds_email_remetente_w,'X') <> 'X') then
	CALL enviar_email(	ds_assunto_w, ds_texto_email_w,ds_email_remetente_w,
			ds_email_dest_p, nm_usuario_p, 'M');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
