CREATE OR REPLACE PROCEDURE pls_enviar_sms_alerta_evento ( ds_remetente_p text, ds_destinatario_p text, ds_mensagem_p text, nr_seq_tipo_evento_p bigint, nm_usuario_p text, id_sms_p INOUT bigint) AS $body$
DECLARE


