BEGIN

if (ds_remetente_p IS NOT NULL AND ds_remetente_p::text <> '') and (ds_destinatario_p IS NOT NULL AND ds_destinatario_p::text <> '') and (ds_mensagem_p IS NOT NULL AND ds_mensagem_p::text <> '') and (nr_seq_tipo_evento_p IS NOT NULL AND nr_seq_tipo_evento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	/* Enviar sms */

	begin
		id_sms_w := wheb_sms.enviar_sms(ds_remetente_p, ds_destinatario_p, ds_mensagem_p, nm_usuario_p, id_sms_w);
	exception
	when others then
		null;
	end;

	insert into log_envio_sms(nr_sequencia, dt_atualizacao, nm_usuario,
		dt_envio, nr_telefone, ds_mensagem,
		id_sms, nr_seq_pls_tipo_evento)
	values (nextval('log_envio_sms_seq'), clock_timestamp(),nm_usuario_p,
		clock_timestamp(), ds_destinatario_p,ds_mensagem_p,
		id_sms_w, nr_seq_tipo_evento_p);

end if;

