SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_envio_email_resc_html ( nr_seq_solicitacao_p bigint, ds_mensagem_p text, ds_assunto_p text, ds_destinatario_p text, ds_anexo_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


cd_prioridade_w			pls_email_parametros.cd_prioridade%type;
nr_seq_email_w			pls_email.nr_sequencia%type;
vl_parametro_w			varchar(255);
ds_anexo_w			varchar(4000);
ds_item_anexo_w			varchar(4000);


BEGIN
--Origem : 2 - OPS - Gestão de Rescisão de Contrato
select	coalesce(max(cd_prioridade),5)
into STRICT	cd_prioridade_w
from	pls_email_parametros
where	ie_origem 		= 2
and	cd_estabelecimento 	= cd_estabelecimento_p
and	ie_situacao 		= 'A';

vl_parametro_w := Obter_Param_Usuario(268, 10, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, vl_parametro_w);

if (nr_seq_solicitacao_p IS NOT NULL AND nr_seq_solicitacao_p::text <> '') then
	insert into pls_email(
				nr_sequencia,
				nr_seq_solic_rescisao,
				cd_estabelecimento,
				nm_usuario_nrec,
				dt_atualizacao_nrec,
				nm_usuario,
				dt_atualizacao,
				ie_tipo_mensagem,
				ie_status,
				ie_origem,
				ds_remetente,
				ds_mensagem,
				ds_destinatario,
				ds_assunto,
				cd_prioridade)
	values (			nextval('pls_email_seq'),
				nr_seq_solicitacao_p,
				cd_estabelecimento_p,
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				2,	--Envio de Dados da Solicitação de rescisão
				'P',	--Pendente
				2,	--OPS - Gestão de Rescisão de Contrato
				vl_parametro_w, 	--Remetente
				ds_mensagem_p,
				ds_destinatario_p,
				ds_assunto_p,
				cd_prioridade_w
				)
		returning nr_sequencia into nr_seq_email_w;

	if (ds_anexo_p IS NOT NULL AND ds_anexo_p::text <> '') then
		ds_anexo_w	:= ds_anexo_p || ',';

		while(ds_anexo_w IS NOT NULL AND ds_anexo_w::text <> '') loop
			ds_item_anexo_w	:= substr(ds_anexo_w,1,position(',' in ds_anexo_w)-1);

			insert into pls_email_anexo(
						nr_sequencia,
						nr_seq_email,
						nm_usuario,
						nm_usuario_nrec,
						dt_atualizacao,
						dt_atualizacao_nrec,
						ds_arquivo,
						ie_tipo_anexo )
			values (			nextval('pls_email_anexo_seq'),
						nr_seq_email_w,
						nm_usuario_p,
						nm_usuario_p,
						clock_timestamp(),
						clock_timestamp(),
						ds_item_anexo_w,
						'A'
						);

			ds_anexo_w	:= substr(ds_anexo_w, position(',' in ds_anexo_w)+1 ,length(ds_anexo_w));
		end loop;
	end if;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
