CREATE OR REPLACE PROCEDURE pls_excecao_preco_pacote ( nr_seq_regra_preco_p bigint, nr_seq_congenere_p bigint, nm_usuario_p text, nr_seq_intercambio_p bigint, nr_seq_prestador_p bigint, ie_excecao_p INOUT text, nr_seq_congenere_prot_p bigint default null) AS $body$
DECLARE


nr_seq_congenere_w		bigint;
nr_seq_regra_preco_w		bigint;
ie_excecao_w			varchar(1)	:= 'N';
ie_grupo_operadora_w    	varchar(1);
nr_seq_grupo_operadora_w    	pls_pacote_tipo_acomodacao.nr_seq_grupo_operadora%type;

C01 CURSOR FOR
	SELECT	a.nr_seq_congenere,
		a.nr_seq_regra_preco,
		a.nr_seq_grupo_operadora
	from	pls_pacote_preco_excecao	a,
		pls_pacote_tipo_acomodacao	b
	where	a.nr_seq_regra_preco 	= nr_seq_regra_preco_p
	and	a.nr_seq_regra_preco	= b.nr_sequencia
	and	((coalesce(a.nr_seq_congenere,0) = coalesce(nr_seq_congenere_p,0))  or (coalesce(a.nr_seq_congenere::text, '') = ''))
	and 	a.ie_situacao		= 'A'
	and	((coalesce(a.nr_seq_intercambio,0) = coalesce(nr_seq_intercambio_p,0))  or (coalesce(a.nr_seq_intercambio::text, '') = ''))
	and	((coalesce(a.nr_seq_prestador,0) = coalesce(nr_seq_prestador_p,0)) or (coalesce(a.nr_seq_prestador::text, '') = ''))
	and	((coalesce(a.dt_inicio_vigencia::text, '') = '') or (a.dt_inicio_vigencia 	<= clock_timestamp()))
	and	((coalesce(a.dt_fim_vigencia::text, '') = '') or (a.dt_fim_vigencia 	>= clock_timestamp()));


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_congenere_w,
	nr_seq_regra_preco_w,
	nr_seq_grupo_operadora_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (nr_seq_grupo_operadora_w IS NOT NULL AND nr_seq_grupo_operadora_w::text <> '') then
		ie_grupo_operadora_w := pls_se_grupo_preco_operadora(nr_seq_grupo_operadora_w,coalesce(nr_seq_congenere_prot_p,nr_seq_congenere_p));
	end if;

	if	((coalesce(nr_seq_grupo_operadora_w::text, '') = '') or (coalesce(ie_grupo_operadora_w,'N') = 'S')) then
		ie_excecao_w	:= 'S';
	end if;
	end;
end loop;
close C01;
ie_excecao_p :=	ie_excecao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
