CREATE OR REPLACE PROCEDURE pls_excluir_alteracao_sib ( nr_sequencia_p bigint, nr_seq_motivo_exclusao_p bigint, nr_seq_lote_sib_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_alteracao_del_w		bigint;/* sequencia da alteração deletada */
nr_seq_segurado_w		bigint;
nr_seq_alteracao_fim_w		bigint;/* sequencia da última alteração */
nr_seq_exclusao_fim_w		bigint;/* sequencia da última exclusão */
qt_segurado_w			bigint;
qt_tipo1_w			bigint;
qt_tipo2_w			bigint;
qt_tipo6_w			bigint;
qt_tipo7_w			bigint;
qt_tipo8_w			bigint;


BEGIN

select	a.nr_sequencia,
	a.nr_seq_segurado
into STRICT	nr_seq_alteracao_del_w,
	nr_seq_segurado_w
from	pls_interf_sib	a
where	a.nr_sequencia = nr_sequencia_p
and	a.nr_seq_lote_sib = nr_seq_lote_sib_p;

/* insere os dados da alteração na tabela sib_log_exclusao que rigistra as alterações excluidas */

insert	into sib_log_exclusao(	nr_sequencia, nr_seq_lote, dt_atualizacao,
				nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
				nr_seq_segurado, nr_seq_motivo_exclusao, dt_registro,
				nm_usuario_registro)
	values (	nextval('sib_log_exclusao_seq'), nr_seq_lote_sib_p, clock_timestamp(),
		nm_usuario_p, clock_timestamp(), nm_usuario_p,
		nr_seq_segurado_w, nr_seq_motivo_exclusao_p, clock_timestamp(),
		nm_usuario_p);

/* verifica se só existe um registro do segurado natabela pls_interf_sib */

select	count(a.nr_sequencia)
into STRICT	qt_segurado_w
from	pls_interf_sib	a
where	a.nr_seq_lote_sib = nr_seq_lote_sib_p
and	a.nr_seq_segurado = nr_seq_segurado_w;

/* exclui o segurado da tabela pls_segurado_sib */

if (qt_segurado_w > 1) then
	delete	from pls_segurado_sib
	where	nr_seq_segurado = nr_seq_segurado_w
	and	nr_seq_lote_sib	= nr_seq_lote_sib_p;
end if;

/* deleta a alteração*/

delete from pls_interf_sib
where  	nr_sequencia	= nr_seq_alteracao_del_w
and	nr_seq_lote_sib	= nr_seq_lote_sib_p; /* 1 = Inclusão /  2 = Alteração / 7 = Exclusão */
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
