CREATE OR REPLACE PROCEDURE pls_excluir_auditoria_regra ( cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_regra_lib_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_regra_liberacao
	where	cd_estabelecimento	= cd_estabelecimento_p;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_regra_lib_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete	FROM pls_lib_grupo_auditor
	where	nr_seq_regra_lib	= nr_seq_regra_lib_w;

	delete	FROM pls_regra_liberacao
	where	nr_sequencia		= nr_seq_regra_lib_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
