CREATE OR REPLACE PROCEDURE pls_excluir_carencia ( nr_seq_tipo_excluir_p bigint, nr_seq_w_carencia_p bigint, nr_seq_tipo_carencia_p bigint, ie_opcao_p text) AS $body$
DECLARE


/*nr_seq_tipo_excluir_p
Parâmetro para qual sequência vai ser excluida.
Do contrato, do beneficiário, beneficiário da proposta, proposta de adesão.
*/
/*
ie_opcao_p
C - Contrato; S - Segurado; P - Beneficiário da proposta, PA - Proposta de adesão
*/
nr_seq_carencia_w	bigint;


BEGIN

if (ie_opcao_p = 'C') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_carencia_w
	from	pls_carencia
	where	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_p
	and	nr_seq_contrato		= nr_seq_tipo_excluir_p;
elsif (ie_opcao_p = 'S') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_carencia_w
	from	pls_carencia
	where	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_p
	and	nr_seq_segurado		= nr_seq_tipo_excluir_p;
elsif (ie_opcao_p = 'P') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_carencia_w
	from	pls_carencia
	where	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_p
	and	nr_seq_pessoa_proposta	= nr_seq_tipo_excluir_p;
elsif (ie_opcao_p = 'PA') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_carencia_w
	from	pls_carencia
	where	nr_seq_tipo_carencia	= nr_seq_tipo_carencia_p
	and	nr_seq_proposta		= nr_seq_tipo_excluir_p;
end if;

if (coalesce(nr_seq_carencia_w,0) <> 0) then
	delete	FROM pls_carencia
	where	nr_sequencia	= nr_seq_carencia_w;
end if;

delete	FROM w_pls_carencia
where	nr_sequencia		= nr_seq_w_carencia_p
and	ie_origem_carencia	<> 'P';

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
