CREATE OR REPLACE PROCEDURE pls_excluir_cco_segurado ( nr_lote_p bigint) AS $body$
DECLARE


nr_seq_segurado_w	pls_segurado.nr_sequencia%type;

c01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	pls_segurado		a,
		pls_segurado_carteira	b,
		sib_devolucao_cco	c,
		pls_lote_sib		d
	where	b.nr_seq_segurado = a.nr_sequencia
	and	b.cd_usuario_plano = c.cd_usuario_plano
	and	c.nr_seq_lote = d.nr_sequencia
	and	d.nr_sequencia = nr_lote_p;


BEGIN

open C01;
loop
fetch c01 into
	nr_seq_segurado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	update	pls_segurado
	set	nr_cco		 = NULL,
		ie_digito_cco	 = NULL,
		cd_cco		 = NULL
	where  	nr_sequencia	= nr_seq_segurado_w;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
