CREATE OR REPLACE PROCEDURE pls_excluir_cliente_solic ( nr_seq_cliente_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_solicitacao_w	bigint;


BEGIN

select	coalesce(max(nr_seq_solicitacao),0)
into STRICT	nr_seq_solicitacao_w
from	pls_comercial_cliente
where	nr_sequencia	= nr_seq_cliente_p;

if (nr_seq_solicitacao_w <> 0) then
	update	pls_solicitacao_comercial
	set	ie_status = 'PE',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_solicitacao_w;
end if;

delete from pls_comercial_historico
where	nr_seq_cliente = nr_seq_cliente_p;

delete from pls_comercial_envio
where	nr_seq_cliente = nr_seq_cliente_p;

delete from pls_comercial_documento
where	nr_seq_cliente = nr_seq_cliente_p;

update	pls_solicitacao_vendedor
set	nr_seq_cliente	 = NULL
where	nr_seq_cliente	= nr_seq_cliente_p;

delete	from	pls_comercial_cliente
where	nr_sequencia	= nr_seq_cliente_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
