SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_excluir_conferencia_sib ( nr_seq_lote_p bigint) AS $body$
DECLARE


qt_registro_sib_vinculado	bigint;
qt_registro_sib_segurado	bigint;
qt_registro_sib_encontrado	bigint;
qt_registro_sib_envio		bigint;
qt_retorno_sib_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_sib_vinculado
from	pls_retorno_sib
where	(nr_seq_segurado_vinculado IS NOT NULL AND nr_seq_segurado_vinculado::text <> '')
and	nr_seq_lote_sib		= nr_seq_lote_p;

select	count(*)
into STRICT	qt_registro_sib_segurado
from	pls_retorno_sib
where	nr_seq_segurado is not  null
and	nr_seq_lote_sib		= nr_seq_lote_p;

select	count(*)
into STRICT	qt_registro_sib_encontrado
from	pls_retorno_sib
where	(nr_seq_segurado_encontrado IS NOT NULL AND nr_seq_segurado_encontrado::text <> '')
and	nr_seq_lote_sib		= nr_seq_lote_p;

select	count(*)
into STRICT	qt_registro_sib_envio
from	pls_retorno_sib
where	ie_enviar_sib	<> 'N'
and	nr_seq_lote_sib		= nr_seq_lote_p;

select	count(*)
into STRICT	qt_retorno_sib_w
from	pls_lote_retorno_sib	a,
	pls_retorno_sib		b,
	pls_sib_tasy_ans	c
where	c.nr_seq_retorno_sib	= b.nr_sequencia
and	b.nr_seq_lote_sib	= a.nr_sequencia
and	a.nr_sequencia		= nr_seq_lote_p;

if (qt_registro_sib_vinculado > 0) or (qt_registro_sib_segurado > 0) or (qt_registro_sib_encontrado > 0) or (qt_registro_sib_envio > 0) or (qt_retorno_sib_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(262234, 'NR_SEQ_LOTE=' || nr_seq_lote_p);
	/* Mensagem: Não é possível excluir o lote NR_SEQ_LOTE! */

else
	delete	FROM pls_retorno_sib
	where	nr_seq_lote_sib	= nr_seq_lote_p;

	delete	FROM w_sib_conferencia
	where	nr_seq_lote_conf = nr_seq_lote_p;

	delete	FROM pls_lote_retorno_sib
	where	nr_sequencia	= nr_seq_lote_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_excluir_conferencia_sib ( nr_seq_lote_p bigint) FROM PUBLIC;

