SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_excluir_conta_web (nr_seq_conta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


/*++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:   Excluir a conta do protoclo na digitação de contas médicas do portal
----------------------------------------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [ x ] Portal [  ] Relatórios [ ] Outros:
 ----------------------------------------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
+++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */qt_contas_ref_w		bigint;
ds_retorno_w		varchar(255) := 'OK';





BEGIN

select	count(*)
into STRICT 	qt_contas_ref_w
from	pls_conta
where	nr_seq_conta_referencia	= nr_seq_conta_p;

if (qt_contas_ref_w = 0) then

	CALL pls_desfazer_consistir_conta(nr_seq_conta_p, cd_estabelecimento_p, nm_usuario_p);

	-- em alguns casos, quando o item é cancelado, alguns logs não são afetados pela pls_desfazer_consistir_conta,
	-- por exemplo as glosas, para se manter um historico durante os processos da conta.
	-- como nesta etapa se deseja de fato excluir tudo, então estes dados serão excluidos independente do status do item
	-- proc
	delete	from pls_conta_glosa	a
	where	a.nr_seq_conta_proc	in (	SELECT	x.nr_sequencia
						from	pls_conta_proc	x
						where	x.nr_seq_conta	= nr_seq_conta_p);

	delete	FROM pls_conta_proc_aprop	a
	where	a.nr_seq_conta_proc	in (	SELECT	x.nr_sequencia
						from	pls_conta_proc	x
						where	x.nr_seq_conta	= nr_seq_conta_p);

	-- mat
	delete	from pls_conta_glosa	a
	where	a.nr_seq_conta_mat	in (	SELECT	x.nr_sequencia
						from	pls_conta_mat	x
						where	x.nr_seq_conta	= nr_seq_conta_p);

	delete	FROM pls_conta_mat_aprop	a
	where	a.nr_seq_conta_mat	in (	SELECT	x.nr_sequencia
						from	pls_conta_mat	x
						where	x.nr_seq_conta	= nr_seq_conta_p);

	delete	from	pls_conta_proc
	where	nr_seq_conta = nr_seq_conta_p;

	delete	from	pls_conta_mat
	where	nr_seq_conta = nr_seq_conta_p;

	delete	from	pls_diagnostico_conta
	where	nr_seq_conta = nr_seq_conta_p;

	delete	from	pls_diagnostico_nasc_vivo
	where	nr_seq_conta = nr_seq_conta_p;

	delete	from	pls_diagnost_conta_obito
	where	nr_seq_conta = nr_seq_conta_p;

	delete	from 	pls_conta
	where	nr_sequencia = nr_seq_conta_p;

	ds_retorno_w := 'OK';
else
	ds_retorno_w := 'Esta conta não pode ser excluída, pois existem registros que dependem da mesma!';
end if;

ds_retorno_p	:=	ds_retorno_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE pls_excluir_conta_web (nr_seq_conta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) FROM PUBLIC;

