SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE pls_excluir_contestacao ( nr_seq_contestacao_p pls_contestacao.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, cd_estabelecimento_p estabelecimento.cd_estabelecimento%type) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
Excluir a constestacao selecionada
-------------------------------------------------------------------------------------------------------------------

Locais de chamada direta: 
[ ]  Objetos do dicionario [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatorios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------

Pontos de atencao:
-------------------------------------------------------------------------------------------------------------------

Referencias:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
				
nr_seq_lote_w			pls_contestacao.nr_seq_lote%type;
ie_status_w			pls_lote_contestacao.ie_status%type;
nr_seq_conta_w			pls_conta.nr_sequencia%type;
qt_contestacao_w		integer;
nr_seq_ptu_fatura_w		pls_lote_contestacao.nr_seq_ptu_fatura%type;
qt_ptu_fatura_encer_w		integer;


BEGIN

if (nr_seq_contestacao_p IS NOT NULL AND nr_seq_contestacao_p::text <> '') then
	select	b.nr_seq_lote,
		a.ie_status,
		b.nr_seq_conta,
		a.nr_seq_ptu_fatura
	into STRICT	nr_seq_lote_w,
		ie_status_w,
		nr_seq_conta_w,
		nr_seq_ptu_fatura_w
	from	pls_contestacao		b,
		pls_lote_contestacao	a
	where	b.nr_seq_lote		= a.nr_sequencia
	and	b.nr_sequencia		= nr_seq_contestacao_p;
	
	if (ie_status_w <> 'A') then
		CALL wheb_mensagem_pck.exibir_mensagem_abort(288987);
	end if;
	
	if (nr_seq_ptu_fatura_w IS NOT NULL AND nr_seq_ptu_fatura_w::text <> '') then
		select	count(1)
		into STRICT	qt_ptu_fatura_encer_w
		from	ptu_fatura
		where	nr_sequencia 	= nr_seq_ptu_fatura_w
		and	ie_status	= 'E';
		
		if (qt_ptu_fatura_encer_w > 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(1113243);
		end if;
	end if;
	
	delete	FROM pls_contest_item_glosa
	where	nr_seq_contest_proc in (	SELECT	nr_sequencia
						from	pls_contestacao_proc
						where	nr_seq_contestacao	= nr_seq_contestacao_p);
						
	delete	FROM pls_contest_item_glosa
	where	nr_seq_contest_mat	 in (	SELECT	nr_sequencia
						from	pls_contestacao_mat
						where	nr_seq_contestacao	= nr_seq_contestacao_p);
						
	delete	FROM pls_contestacao_proc
	where	nr_seq_contestacao	= nr_seq_contestacao_p;

	delete	FROM pls_contestacao_mat
	where	nr_seq_contestacao	= nr_seq_contestacao_p;

	delete	FROM pls_contestacao
	where	nr_sequencia		= nr_seq_contestacao_p;

	CALL pls_atualizar_valores_contest(nr_seq_lote_w,'N');
	
	-- Log Exclusao
	pls_gerar_contest_log( nr_seq_lote_w, null, nr_seq_conta_w, null, null, 'PLS_EXCLUIR_CONTESTACAO', 'EXC', 'N', nm_usuario_p);
		
	select	count(1)
	into STRICT	qt_contestacao_w
	from	pls_contestacao
	where	nr_seq_lote	= nr_seq_lote_w;
		
	if (qt_contestacao_w = 0) then
		update 	titulo_pagar_baixa
		set	nr_seq_pls_lote_contest	 = NULL
		where 	nr_seq_pls_lote_contest	= nr_seq_lote_w;
	end if;
	
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
