CREATE OR REPLACE PROCEDURE pls_excluir_declaracao_saude ( nr_seq_declaracao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_declaracao_w	bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	pls_declaracao_segurado
	where	nr_seq_declaracao	= nr_seq_declaracao_p;


BEGIN

delete	from	pls_declaracao_resposta
where	nr_seq_declaracao	= nr_seq_declaracao_p;

delete	from	pls_declaracao_complemento
where	nr_seq_declaracao	= nr_seq_declaracao_p;

delete	from	pls_declaracao_result
where	nr_seq_avaliacao	= nr_seq_declaracao_p;

open C01;
loop
fetch C01 into
	nr_seq_declaracao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	delete	from	pls_declaracao_resposta
	where	nr_seq_declaracao	= nr_seq_declaracao_w;

	delete	from	pls_declaracao_complemento
	where	nr_seq_declaracao	= nr_seq_declaracao_w;

	delete	from	pls_declaracao_result
	where	nr_seq_avaliacao	= nr_seq_declaracao_w;

	delete	from	pls_declaracao_segurado
	where	nr_sequencia	= nr_seq_declaracao_w;
	end;
end loop;
close C01;

delete	from	pls_declaracao_segurado
where	nr_sequencia	= nr_seq_declaracao_p;
commit;

end;
