CREATE OR REPLACE PROCEDURE pls_excluir_dev_erro_sib ( nr_seq_lote_sib_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_dev_erro_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	sib_devolucao_erro
	where	nr_seq_lote	= nr_seq_lote_sib_p
	and	ie_xml		= 'S';


BEGIN


open C01;
loop
fetch C01 into
	nr_seq_dev_erro_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	delete	FROM sib_dev_erro_conteudo
	where	nr_seq_devolucao_sib	= nr_seq_dev_erro_w;

	delete	FROM sib_devolucao_erro
	where	nr_sequencia		= nr_seq_dev_erro_w;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
